import React from "react";
import AutoCompleteResults from "./AutoCompleteResults";
import '../App.css'

const Filter = ({handleNewSearch, autocomplete, showAutocomplete}) => {


    //These two show or hide the autocomplete search terms if input is in focus or not
    const handleFocusOn = () => {
        showAutocomplete = true
    }

    const handleFocusOff = () => {
        showAutocomplete = false
    }

    return (
        <div className="searchBar">
            <input onChange={handleNewSearch} onFocus={handleFocusOn} onBlur={handleFocusOff}/>

            {showAutocomplete ? 
            <AutoCompleteResults autocomplete={autocomplete}/>
            : <></>
            }

        </div>
    )
}

export default Filter;
