import React, { useState, useEffect } from "react";

const Hourly = ({hourlyData}) => {

    const [ hourArray, setHourArray ] = useState([])

    useEffect(() => {

        const tempArray = []

        for (let i=0; i<24; i++) {
            const unixDate = new Date(hourlyData[i].dt * 1000)
            const time = `${unixDate.getHours()}`

            const icon = hourlyData[i].weather[0].icon

            const temp = Math.round(hourlyData[i].temp)


            const hourWeather = {
                Time: time,
                Icon: icon,
                Temp: temp
            }

            tempArray.push(hourWeather)

        }
        setHourArray(tempArray);

    }, [])



    return (
        <div className="hourly-wrapper">
            {hourArray.map((hour, Idx) => {
                <li>{hour.temp} {hour.icon} {hour.time}</li>
            })}
        </div>
    )
}

export default Hourly;
