import React, { useState, useEffect } from "react";

const Hourly = ({hourlyData}) => {

    const padNumber = (numberToPad) => {
        return numberToPad.toString().padStart(2, '0')
    }

    const tempArray = []

    for (let i=0; i<24; i++) {
        const unixDate = new Date(hourlyData[i].dt * 1000)
        const time = padNumber(`${unixDate.getHours()}`)

        const icon = hourlyData[i].weather[0].icon

        const temp = Math.round(hourlyData[i].temp)


        const hourWeather = {
            time: time,
            icon: icon,
            temp: temp
        }

        tempArray.push(hourWeather)

    }
    console.log(tempArray);




    return (
        <div className="hourly-wrapper">
            {tempArray.map((hour, Idx) => (
                <div className="hourly-hour">
                    <div>{hour.time}</div>
                    <div><img src={`http://openweathermap.org/img/wn/${hour.icon}@2x.png`} height={'50px'}></img></div>
                    <div>{hour.temp}{`\u00B0`}C</div>
                </div>
            ))}
        </div>
    )
}

export default Hourly;
