import React from "react";

const Daily = ({dailyData}) => {

    const dailyArray = []

    for (let i=0; i< 7; i++) {
        const week = ['Sunday','Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
        const unixDate = new Date(dailyData[i].dt * 1000)
        let dayName = ""

        if (i === 0) {
            dayName = "Today"
        }
        else {
            dayName = week[ unixDate.getDay() ] 
        }

        const icon = dailyData[i].weather[0].icon

        const rainProb = dailyData[i].pop

        const minTemp = Math.round(dailyData[i].temp.min)
        const maxTemp = Math.round(ailyData[i].temp.max)

        const dayObject = {
            dayName: dayName,
            icon: icon,
            rainProb: rainProb,
            minTemp: minTemp,
            maxTemp: maxTemp
        }
        dailyArray.push(dayObject)
        console.log(dayObject);
    }



    return (
        <div className="daily-wrapper">
            {dailyArray.map((day, Idx) => (
                <div className="daily-day">
                    <div>{day.dayName}</div>
                    <div><img src={`http://openweathermap.org/img/wn/${day.icon}@2x.png`}></img></div>
                    <div className="daily-day-temps">
                        <div>{day.minTemp}</div>
                        <div>{day.maxTemp}</div>
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Daily;
