import React, { useEffect } from "react";

const Hourly = ({hourlyData}) => {

    useEffect(() => {

        const hourArray = []

        for (let i=0; i<24; i++) {
            const unixDate = new Date(hourlyData[i].dt * 1000)
            const time = `${unixDate.getHours()}:${unixDate.getMinutes()}`

            const icon = hourlyData.weather.icon

            const temp = Math.round(hourlyData[i].temp)


            const hourWeather = {
                Time: time,
                Icon: icon,
                Temp: temp
            }

            hourArray.push(hourWeather)

        }


    }, [])



    return (
        <div className="hourly-wrapper">
            {}
        </div>
    )
}

export default Hourly;
